// sql语句及执行的实体参数，实体参数可以是实体类型或者list《实体类型》
//示例1：
Dictionary《string, object》 dic = new Dictionary《string, object》();
Person p1 = new Person { id1 = 1, Name = "张三", Remark = DateTime.Now.ToString() };
string sql = "insert into Person (id, Name, Remark ) values(:ID1, :Name, :Remark) ";
dic.Add(sql, p1);
Person p2 = new Person { id1 = 2, Name = "李四" };
sql = "update Person set Name=:Name where id=:ID1 ";
dic.Add(sql, p2);
var rtn = ExecuteTransaction(dic);

//示例2：
Dictionary 《string, object》 dic = new Dictionary《string, object》();
Person p1 = new Person { id1 = 1, Name = "张三", Remark = DateTime.Now.ToString() };
Person p2 = new Person { id1 = 2, Name = "李四", Remark = DateTime.Now.ToString() };
List《object》 ps = new List《object》();
ps.Add(p1);
ps.Add(p2);
string sql = "insert into Person (id, Name, Remark ) values(:ID1, :Name, :Remark) ";
dic.Add(sql, ps);
var rtn = ExecuteTransaction(dic);
